function y = conv_coord_40k(x)%information from gps.gov.uk, AIRY 1830for r=1:length(x)    Lon1 = x(r,1)*pi/180;     Lat1 = x(r,2)*pi/180;     a = 6377563.396;    b = 6356256.910;    F0 = 0.9996012717;    Lat0 = 49*pi/180;    Lon0 = -2*pi/180;    E0 = 400000;    N0 = -100000;    e2 = (a^2-b^2)/a^2;    n = (a-b)/(a+b);    v = a*F0/(sqrt(1-(e2*(sin(Lat1))^2)));    p = (v*(1-e2))/(1-(e2*(sin(Lat1))^2));    eta2 = (v/p)-1;    P = Lon1-Lon0;    Q = Lat1-Lat0;    QQ = Lat1+Lat0;    M = b*F0*((1+n+1.25*(n^2+n^3))*Q-(3*(n+n^2)+2.625*n^3)*sin(Q)*cos(QQ)+1.875*n^2*(1+n)*sin(2*Q)*cos(2*QQ)-35/24*n^3*sin(3*Q)*cos(3*QQ));    I = M+N0;    II = (v/2)*(sin(Lat1))*(cos(Lat1));    III = ((v/24)*(sin(Lat1))*((cos(Lat1))^3))*(5-((tan(Lat1))^2)+(9*eta2));    IIIA = ((v/720)*(sin(Lat1))*((cos(Lat1))^5))*(61-(58*((tan(Lat1))^2))+((tan(Lat1))^4));    IV = v*(cos(Lat1));    V = (v/6)*((cos(Lat1))^3)*((v/p)-((tan(Lat1)^2)));    VI = (v/120)*((cos(Lat1))^5)*(5-(18*((tan(Lat1))^2))+((tan(Lat1))^4)+(14*eta2)-(58*((tan(Lat1))^2)*eta2));    y(r,1) = E0+(P*IV)+((P^3)*V)+((P^5)*VI)+106; %Hort y(r,1) %106 and 40 is getmapping displacement    y(r,2) = I+((P^2)*II)+((P^4)*III)+((P^6)*IIIA)-40; %Vert y(r,2)end
w=[0.2, -0.2; -429481.5, -233295.5]; %5m res, eastings/northings of top left pixel
y(:,1)=w(1,1)*(y(:,1) + w(2,1));y(:,2)=w(1,2)*(y(:,2) + w(2,2));
